var crypto = require('crypto');
var rand = require('csprng');
var mongoose = require('mongoose');
var gravatar = require('gravatar');
var user = require('functions/models');

exports.check = function(pno,callback) {



    user.find({'pno':pno},function(err,users){

        if(users.length != 0){
            callback({'response':"Phone number is already registered",'res':true});
        }
        else{
            callback({'response':"Phone number is new",'res':false});
        }

    });
}
