var crypto = require('crypto');
var rand = require('csprng');
var mongoose = require('mongoose');
var gravatar = require('gravatar');
var order = require('functions/modelos');

exports.getOr = function(pno,callback) {



    order.find({'pNo':pno},function(err,orders){

        if(orders.length != 0){

            var i=0;
            for( i=0; i<orders.length;i++)
            {
                var phone = orders[i].pNo;
                var orderID = orders[i].orderID;
            }


            if(phone == pno){

                callback({'response':orderID,'res':true,});

            }else{

                callback({'response':"Database Error",'res':false});

            }
        }
        else {

            callback({'response':"User does not exist",'res':false});

        }
    });
}
