var crypto = require('crypto');
var rand = require('csprng');
var mongoose = require('mongoose');
var gravatar = require('gravatar');
var user = require('functions/models');

exports.login = function(pno,pword,callback) {



    user.find({'pno':pno},function(err,users){

        if(users.length != 0){


            var phone = users[0].pno;
            var pass = users[0].password;


            if(phone == pno && pass==pword){

                callback({'response':"Login Sucess",'res':true});

            }else{

                callback({'response':"Invalid Password",'res':false});

            }
        }
        else {

            callback({'response':"User not exist",'res':false});

        }
    });
}
